		function HitkadmutBar() {
			Hitkadmut = new lib.Hitkadmut();
			Hitkadmut.x = 960;
			Hitkadmut.y = 57;
			Hitkadmut.name = "Hitkadmut";
			stage.addChild(Hitkadmut);
			hitkadmutBarMask(gameChoice != null ? (247.3 * answeredQ.length / questions[gameChoice].length) : 0);
			Hitkadmut.HitkadmutNum.text = trueAnswers + " / " + questions[gameChoice].length
		}
		
		function hitkadmutBarMask(partWidth) {
			var masky = new createjs.Shape(new createjs.Graphics().beginFill("#ff0000").drawRect(Hitkadmut.HitkadmutMad.x - 247.3 / 2, Hitkadmut.HitkadmutMad.y - 30, partWidth, 50, 0, 0));
			Hitkadmut.HitkadmutMad.mask = masky;
		}
		
		function checkHitkadmut(partOfHitkadmut = 0) {
		
			var partWidth = (((answeredQ.length - 1) / questions[gameChoice].length) * 247.3) +
				((247.3 * partOfHitkadmut) / (10 * questions[gameChoice].length));
			clearInterval(hitkadmutInterval)
			if (partOfHitkadmut == 10) {
				hitkadmutInterval = null
			} else {
				hitkadmutBarMask(partWidth);
				hitkadmutInterval = setInterval(function () {
					checkHitkadmut(partOfHitkadmut + 1);
				}, 10);
			}
		
		}
		
