		function Timer() {
			clearInterval(myinterval)
			myinterval = setInterval(again, 1000)
			function again() {
				if (!ispused) {
					numSec--
					TimerClock.TimerTxt.text = numSec;
		
					if (numSec == 0) {
						clearInterval(myinterval)
						TimeOver();
		
					}
				}
			}
			var TimerClock = new lib.TimerClock();
			TimerClock.x = 1150;
			TimerClock.y = 15;
			TimerClock.name = "TimerClock";
			stage.addChild(TimerClock);
		}
		
		
